import timeit
'''
 * Cracking the coding interview edition 6
 * Example string aabcccccaaa should be compressed to a2b1c5a3,
 * however if compressed string is bigger than original string, return original string

'''
string = ""
def stringCompress_join()->str:
    
    n = len(string)

    if(n<2):
        return string
    
    res = []
    count = 1
    for i in range(1,n):
        if(string[i-1] == string[i]):
            count += 1
        else:
            res.append(string[i-1])
            res.append(str(count))
            count = 1
        
        if len(res) >= n:
            return string
    
    res.append(string[n-1])
    res.append(str(count))

    if(len(res) >= n):
        return string
    
    return "".join(res)

def stringCompress_concat()->str:
    
    #string = ""
    n = len(string)
    if(n<2):
        return string
    
    res = ""
    count = 1
    for i in range(1,n):
        if(string[i-1] == string[i]):
            count += 1
        else:
            res += (string[i-1])
            res += (str(count))
            count = 1
        
        if len(res) >= n:
            return string
    
    res += (string[n-1])
    res += (str(count))

    if(len(res) >= n):
        return string
    
    return res


tim1 = timeit.timeit(stringCompress_join, number=10)
res = stringCompress_join()
print(res)
print("join time :" + str(tim1))

tim2 = timeit.timeit(stringCompress_concat, number=10)
res2 = stringCompress_concat()
print(res2)
print("concat time :" + str(tim2))
    

